(window.webpackJsonp=window.webpackJsonp||[]).push([[113],{490:function(_,v,t){"use strict";t.r(v);var l=t(25),p=Object(l.a)({},(function(){var _=this,v=_.$createElement,t=_._self._c||v;return t("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[t("h2",{attrs:{id:"《重生之心动》"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#《重生之心动》"}},[_._v("#")]),_._v(" 《重生之心动》")]),_._v(" "),t("ul",[t("li",[t("p",[_._v("作者："),t("strong",[_._v("初恋璀璨如夏花")])]),_._v(" "),t("blockquote",[t("p",[_._v("许多人都有过重生的念头，如果有来生这句话已经说了几千年，虽然这只是一个故事，但在心里我们应该有这样的一场梦幻。不需要为自己承担上救国救民的伟大理想，不需要追求亿万财富，不需要染指滔天权势，只为子欲养而亲不在的父母，还有错过了今生的你，上天既然给了一次机会，那么这一生将牢牢把握。")])]),_._v(" "),t("blockquote",[t("p",[_._v("秦安回到初三开学的第一天，他向一直暗恋着的叶竹澜表白了。在这个会有“第八套全国中小学生广播体操，原地踏步走……”的时代，在这个会有“为了革命保护视力……”的时代，在三八线依然横亘在少男少女课桌上的时代，秦安所最迫切要做的并不是因为成绩太差被扫地出门，换班后发奋学习一扫前耻，而是如何安慰叶竹澜。\n叶竹澜，秦安十三岁的初恋情人，正被秦安始料未及的大胆表白弄得胆颤心惊，她被吓坏了。\n那是一个纯纯的年代，你的手指触碰到我的掌心，便会脸红心跳一整天的时代。")])])]),_._v(" "),t("li",[t("p",[_._v("创作时间："),t("strong",[_._v("2010~2011")])])]),_._v(" "),t("li",[t("p",[_._v("男主：秦安")]),_._v(" "),t("ul",[t("li",[t("p",[_._v("身份：老师的儿子")])]),_._v(" "),t("li",[t("p",[_._v("年龄：十四岁")])]),_._v(" "),t("li",[t("p",[_._v("金手指：重生")])]),_._v(" "),t("li",[t("p",[_._v("嗜好：女")])])])]),_._v(" "),t("li",[t("p",[_._v("女主：")]),_._v(" "),t("ol",[t("li",[t("p",[_._v("叶竹澜 女主角之一，小名“叶子”，天真可爱，主角初恋。前世因异地上学，以及秦安与孙荪的一张亲昵照片的误会，与秦安渐行渐远。因一些流言（与别的男人关系暧昧，时常去酒吧，寻一夜情），被秦安认为堕落了，后被叶子前世灵魂证实是假的。极爱秦安，因精神原因开车自杀，压断了秦安心里最后一根稻草，精神恍惚而重生。这一世受到秦安最多的关爱，一直保留天真可爱的天性。")])]),_._v(" "),t("li",[t("p",[_._v("孙荪 女主角之一，主角初三同桌，秦安的“瓷人儿”，爱好唱歌。前世为大明星，是秦安的红颜知己，受奸人迫害自杀而死，给秦安带来了严重的精神打击。今生受秦安保护，专为她开了家娱乐公司。")])]),_._v(" "),t("li",[t("p",[_._v("廖瑜 女主角之一，小名“小鱼儿”，主角初中老师，风韵十足的少妇，初登场23岁，后成为主角情人。")])]),_._v(" "),t("li",[t("p",[_._v("李淑月 女主角之一，主角的堂嫂，丈夫死于矿难。后倾心于主角。")])]),_._v(" "),t("li",[t("p",[_._v("安水 女主角之一，初登场18岁，与主角在青山镇老宅相见。")])]),_._v(" "),t("li",[t("p",[_._v("安洛（唐媚） 女主角之一，主角前世妻子，安水的妹妹，孕有一女秦安澜。同样是重生者，大约比主角重生的时间早一些，凭借重生者的优势，把安家的产业扩展了无数。对主角的感情无比深厚。化装为唐媚进入市一中与主角同班，直到小说后期才表明身份。嫉妒心较重，重名分，认为秦安是她一个人的。因秦安与孙荪前世密切的关系，视她为“狐狸精”。在外传秦安澜中，与秦安生下了与前世一样的女儿安澜，才决定接受秦安的其他女人。")])]),_._v(" "),t("li",[t("p",[_._v("李心蓝 女主角之一，邻家女孩，在正文中出现较少，不过外传中与秦安在一起了。")])]),_._v(" "),t("li",[t("p",[_._v("秦沁 秦安侄女，李淑月之女，极度依恋秦安。外传中十八岁生日向秦安告白。")])])])]),_._v(" "),t("li",[t("p",[_._v("雷&郁闷：")]),_._v(" "),t("ul",[t("li",[t("p",[_._v("重生之后温馨的感觉，节奏肯定会慢很多")])]),_._v(" "),t("li",[t("p",[_._v("13岁的初中生男主搞女班主任，说道,第一次插，我要留给同龄的某某，你能理解吧；女老师无限温柔道，我理解，你用手，我用嘴，就已经很满足了，你这个好人，让人家吃那么多蛋白质，美容养颜，一定不要让我离开你额。。。。好吧，说句心里话，我只想说，请降天雷滚滚，劈死作者吧\n《重生之心动》——大神哎卓娃的书评")])])]),_._v(" "),t("p",[_._v("优书网链接：https://www.yousuu.com/book/115/comment/56a97b6bd975c0a112cff365/")])]),_._v(" "),t("li",[t("p",[_._v("爽点：")]),_._v(" "),t("ul",[t("li",[t("p",[_._v("正如作者笔名，初恋璀璨如夏花，相当于夏洛特烦恼，初恋不是自己老婆的可以好好YY一下😏")])]),_._v(" "),t("li",[t("p",[_._v("夏花的温暖向后宫，暖暖的，甜甜的，文笔、妹子刻画、感情描写绝对是后宫类顶尖水平了。但这种文风看久了也容易腻，这本书看了大半，审美疲劳了，公主初看惊艳，但仅几十章，腻味了，书荒试看n次，始终进入不了状态，当时不明白，觉得文笔，人物，剧情我都很喜欢，为什么就是看不下去呢，现在明白，那种文字就是一种“模仿女孩子撒娇”感觉，字里行间都透露着一种“我好可爱，快来夸我啊”。\n《重生之心动》——司马青衣11的书评\n优书网链接：https://www.yousuu.com/book/115/comment/55d8730bd1cfbb3c0bb6a5f5/")])])])]),_._v(" "),t("li",[t("p",[_._v("扫书贴：")]),_._v(" "),t("ul",[t("li",[t("a",{attrs:{href:"https://tieba.baidu.com/p/5587277211",target:"_blank",rel:"noopener noreferrer"}},[_._v("https://tieba.baidu.com/p/5587277211"),t("OutboundLink")],1)])])])])])}),[],!1,null,null,null);v.default=p.exports}}]);